const numberOfAdsToTreeSaved = (numberOfAds) => {
  switch (numberOfAds) {
    case 0: {
      return 1 / 50;
    }
    case 1: {
      return 1 / 10;
    }
    case 2: {
      return 1 / 8;
    }
    case 3: {
      return 1 / 5;
    }
    default: {
      // Return treesSaved itself, if source is sponsored-survey
      return numberOfAds;
    }
  }
};

